import {useState} from 'react';
import './App.css'
import Cards from './components/cards/cards'

function App() {
  
  const [item, setObjet] = useState([
    {
      nom : 'Erable de Cappadoce',
      prix : 6.70,
      latin : 'Acer Cappadocicum',
      detail : '60/80 cm pot 1L',
      img : 'https://www.jardins-du-monde.be/1802-thickbox_default/erable-de-cappadoce.jpg'
    },
    {
      nom : "Copalme d'Amérique",
      prix : 7.50,
      latin : 'Liquidambar styraciflua',
      detail : '70/80 cm pot 1L',
      img : 'https://www.jardins-du-monde.be/358-thickbox_default/copalme-d-amerique.jpg'
    },
    {
      nom : 'Magnolia de Kobé',
      prix : 7.75,
      latin : 'Magnolia'
    }
  ])

  const [argent, setArgent] =useState(20)
  const acheter = (produit) => {
    setArgent(argent - produit.prix)
    produit.unite -= 1
    produit.quantite += 1
  }


  return (
    <div className="App">
     <div className='cardsmap'>
       {item.map((item)=> (<Cards image={item.img} name={item.nom} prix={item.prix} achat={()=>acheter(item)} money={argent}/>))}
     </div>
    </div>
  );
}

export default App;
