import {useState} from 'react';
import './App.css'
import Cards from './components/cards/cards'

function App() {
  
  const [item, setObjet] = useState([
    {
      nom : 'Erable de Cappadoce',
      prix : 6.70,
      img : 'https://www.pepinieres-minier.fr/sites/default/files/produits/450-620/6146-01.jpg'
    },
    {
      nom : "Copalme d'Amé"
    }
  ])

  const [argent, setArgent] =useState(20)
  const acheter = (produit) => {
    setArgent(argent - produit.prix)
    produit.unite -= 1
    produit.quantite += 1
  }


  return (
    <div className="App">
     <div className='cardsmap'>
       {item.map((item)=> (<Cards image={item.img} name={item.nom} prix={item.prix} achat={()=>acheter(item)} money={argent}/>))}
     </div>
    </div>
  );
}

export default App;
